    private void bhapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bhapusActionPerformed
        // TODO add your handling code here:
        int row = tbrt.getSelectedRow();
        if(row!= -1){
            String cell = tbrt.getModel().getValueAt(row, 0).toString();
            String nomer = tbrt.getValueAt(row, 0).toString();

            String sql = "DELETE FROM dt_erte where id='"+nomer+"'";
            String resetno="ALTER TABLE dt_erte DROP `id`;";
            String consecutivenumber="ALTER TABLE dt_erte ADD `id` INT(11) NOT NULL AUTO_INCREMENT FIRST ,ADD KEY(`id`);";

            if (JOptionPane.showConfirmDialog(null, "HAPUS DATA INI..??"
                ,"KONFIRMASI",JOptionPane.OK_CANCEL_OPTION,
                JOptionPane.INFORMATION_MESSAGE)== JOptionPane.OK_OPTION) {

            try {
                conn.createStatement().execute(sql);
                conn.createStatement().execute(resetno);
                conn.createStatement().execute(consecutivenumber);

                JOptionPane.showMessageDialog(null, "Data Berhasil Dihapus");
                control.isiTableRT();
                control.reset();

            } catch (Exception e) {
                JOptionPane.showMessageDialog(null, e);
            }
        }else{
            JOptionPane.showMessageDialog(null, "Klik Salah Satu Tabel");
        }
        }
    }//GEN-LAST:event_bhapusActionPerformed

