    private void tbrtKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_tbrtKeyReleased
        // TODO add your handling code here:
        int row = tbrt.getSelectedRow();
   if (evt.getKeyCode()== KeyEvent.VK_ENTER) {
//         String no = (String)tbrt.getValueAt(row,0);
//     txtno.setText(no);
     String kode = (String)tbrt.getValueAt(row,1);
     txtkodert.setText(kode);
     String ketua = (String)tbrt.getValueAt(row,2);
     txtketua.setText(ketua);
     String wakil = (String)tbrt.getValueAt(row,3);
     txtwakil.setText(wakil);
     String sekretaris = (String)tbrt.getValueAt(row,4);
     txtsekretaris.setText(sekretaris);
     String bendahara = (String)tbrt.getValueAt(row,5);
     txtbendahara.setText(bendahara);
     Object status = (Object)tbrt.getValueAt(row,6);
     cbstatus.setSelectedItem(status.toString());
   
       control.update();
        control.isiTableRT();
        control.reset();
         }
    }//GEN-LAST:event_tbrtKeyReleased


