    public void exit(){
         int konfirmasi = JOptionPane.showConfirmDialog(
                this, "Konfirmasi Keluar Aplikasi",
                "Yakin Ingin Keluar Dari Program",
                JOptionPane.YES_NO_OPTION,
                JOptionPane.QUESTION_MESSAGE);
        
        if (konfirmasi== JOptionPane.YES_OPTION) {
            System.exit(0);
            
        }
    }

