    private void txtPasswordKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtPasswordKeyPressed
        // TODO add your handling code here:
       if (evt.getKeyCode()== KeyEvent.VK_ENTER) {
        String userName, passWd;
        boolean status;
        userName = txtUsername.getText();
        passWd = String.valueOf(txtPassword.getPassword());

        ModelSiap login = new ModelSiap();
        
        login.setUsername(userName);
        login.setPassword(passWd);

        ControllerLogin userMan = new ControllerLogin();

        try {
            status = userMan.cekLogin(login);
            if(status == true) { 
                JOptionPane.showMessageDialog(rootPane, "Login Berhasil");
                
                DashboardUtama das = new DashboardUtama();
                das.setVisible(true); 
                this.dispose();
                
            }else{
                JOptionPane.showMessageDialog(rootPane, "Login Gagal");
            }
        } catch (SQLException ex) {
            Logger.getLogger(LoginAplikasi.class.getName()).log(Level.SEVERE, null, ex);
        }
          }
    }//GEN-LAST:event_txtPasswordKeyPressed

