    private void jButton9ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton9ActionPerformed
        // TODO add your handling code here:
        //        Layar.pindahPanel(jPanel19, "card6");
        //        TableRowSorter<TableModel>sorter = new TableRowSorter<TableModel>(((DefaultTableModel)tbpindah.getModel()));
        //        sorter.setRowFilter(RowFilter.regexFilter(txtcarikk.getText()));
        //        tbpindah.setRowSorter(sorter);
        String kode, kk;
        boolean status;
        kode = txtkodepdh.getText();
        kk = cbkk.getSelectedItem().toString();
        ModelSiap pdh = new ModelSiap();

        pdh.setKode_pindah(kode);
        pdh.setNo_kk(kk);

        ModelPindah userMan = new ModelPindah();

        try {
            status = userMan.cekPindah(pdh);
            if(status == true) {
                JOptionPane.showMessageDialog(rootPane, "Form Input Warga Pindah");
                TambahWargaPindah list = new TambahWargaPindah(this);
                this.getParent().add(list);
                list.setVisible(true);
                control.reset();
            }else{
                JOptionPane.showMessageDialog(rootPane, "Gagal Masuk Form Pindah");
            }
        } catch (SQLException ex) {
            Logger.getLogger(ViewDataPindah.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_jButton9ActionPerformed

